package week1.day1;

public class Primenumber {

	public static void main(String[] args) {
		int n=6;
		for(int i=2;i<=n-1;i++) {
			if(n%i == 0) {
				System.out.println("This number is prime");
			}
				else {
					System.out.println("this number is not prime");
				
			}
			
		}
		// TODO Auto-generated method stub

	}

}
